    AND ${param} IS NOT NULL AND ${param} <> '' AND ${param} <> 'NA'



  ORDER BY ${cause}

select

from animals


    AND Species = #{species}


    AND Sample_site = #{sampleSite1}


    AND Instrument = #{instrument}


    AND Library_Strategy = #{libraryStrategy}


    AND Bioproject_type like CONCAT('%', #{bioprojecttype}, '%')

select

from animals


    AND Species = #{species}


    AND Genus = #{genus}


    AND Bioproject_type like CONCAT('%', #{bioprojecttype}, '%')


    AND Outline = #{gang}


    AND Orders = #{mu}


    AND Family = #{ke}


    AND Genus = #{shu}


    AND Species = #{zhong}


GROUP BY Bioproject_Accession
order by Bioproject_Accession

select SUBSTRING_INDEX((SUBSTRING_INDEX(Bioproject_type,'-',2)),'-',-1)  as Second_clo from animals
where SUBSTRING_INDEX(Bioproject_type,'-',1) = #{FirstClo}
group by Second_clo order by Second_clo

select

from animals


    AND Species <> 'Bovine' AND Species <> 'Horse' AND Species <> 'Pig' AND Species <> 'Sheep'


    AND Species = 'Bovine' OR Species = 'Horse' OR Species = 'Pig' OR Species = 'Sheep'


    AND Species = #{species}


    AND Genus = #{genus}


    AND Bioproject_type like CONCAT('%', #{bioprojecttype}, '%')


    AND Outline = #{gang}


    AND Orders = #{mu}


    AND Family = #{ke}


    AND Genus = #{shu}


    AND Species = #{zhong}


    AND Study_type = #{sampleSite0}


    AND Sample_site = #{sampleSite1}


    AND ${param} IS NOT NULL AND ${param} <> '' AND ${param} <> 'NA'


GROUP BY Sample_Accession

  ORDER BY ${cause}

select

from animals


    AND Species <> 'Bovine' AND Species <> 'Horse' AND Species <> 'Pig' AND Species <> 'Sheep'


    AND Species = 'Bovine' OR Species = 'Horse' OR Species = 'Pig' OR Species = 'Sheep'


    AND Species = #{species}


    AND Genus = #{genus}


    AND Bioproject_type like CONCAT('%', #{bioprojecttype}, '%')


    AND Outline = #{gang}


    AND Orders = #{mu}


    AND Family = #{ke}


    AND Genus = #{shu}


    AND Species = #{zhong}


    AND Study_type = #{sampleSite0}


    AND Sample_site = #{sampleSite1}


GROUP BY Sample_Accession
order by Sample_Accession

select distinct species
from animals


    AND Species <> 'Bovine' AND Species <> 'Horse' AND Species <> 'Pig' AND Species <> 'Sheep'


    AND Species = 'Bovine' OR Species = 'Horse' OR Species = 'Pig' OR Species = 'Sheep'


    AND Species = #{species}


    AND Genus = #{genus}


    AND Bioproject_type like CONCAT('%', #{bioprojecttype}, '%')


    AND Outline = #{gang}


    AND Orders = #{mu}


    AND Family = #{ke}


    AND Genus = #{shu}


    AND Species = #{zhong}


    AND Study_type = #{sampleSite0}


    AND Sample_site = #{sampleSite1}


order by species

SELECT DISTINCT IFNULL(Study_type,'NA') AS Sample_site0,Sample_site AS Sample_site1
FROM animals


    AND Species <> 'Bovine' AND Species <> 'Horse' AND Species <> 'Pig' AND Species <> 'Sheep'


    AND Species = 'Bovine' OR Species = 'Horse' OR Species = 'Pig' OR Species = 'Sheep'


    AND Species = #{species}


    AND Genus = #{genus}


    AND Bioproject_type like CONCAT('%', #{bioprojecttype}, '%')


    AND Outline = #{gang}


    AND Orders = #{mu}


    AND Family = #{ke}


    AND Genus = #{shu}


    AND Species = #{zhong}


    AND Study_type = #{sampleSite0}


    AND Sample_site = #{sampleSite1}


ORDER BY Sample_site0,Sample_site1

SELECT DISTINCT
SUBSTRING_INDEX(Bioproject_type,'-',1) as level1,
SUBSTRING_INDEX(SUBSTRING_INDEX(Bioproject_type,'-',2),'-',-1) as level2,
SUBSTRING_INDEX(SUBSTRING_INDEX(Bioproject_type,'-',3),'-',-1) as level3
FROM animals


    AND Species <> 'Bovine' AND Species <> 'Horse' AND Species <> 'Pig' AND Species <> 'Sheep'


    AND Species = 'Bovine' OR Species = 'Horse' OR Species = 'Pig' OR Species = 'Sheep'


    AND Species = #{species}


    AND Genus = #{genus}


    AND Bioproject_type like CONCAT('%', #{bioprojecttype}, '%')


    AND Outline = #{gang}


    AND Orders = #{mu}


    AND Family = #{ke}


    AND Genus = #{shu}


    AND Species = #{zhong}


    AND Study_type = #{sampleSite0}


    AND Sample_site = #{sampleSite1}


ORDER BY level1,level2,level3

